import { DefaultText } from '@/components/DefaultText'
import { TouchableView } from '@/components/TouchableView'
import { GlobalState } from '@/modules'
import { shadow } from '@/style/style-util'
import { DefaultTheme } from '@/style/styled'
import { isLight } from '@/style/themes'
import { IProgram } from '@/utils/data'
import React, { useState } from 'react'
import { GestureResponderEvent, StyleSheet, Text } from 'react-native'
import { FlatList, View } from 'react-native'
import { useSelector } from 'react-redux'
import Schedule from './Schedule'

interface ProgramProps {
  data: IProgram[]
  isLoading: Boolean
}

function Program({ data, isLoading }: ProgramProps) {
  const [modalVisible, setModalVisible] = useState(false)
  //스타일 설정
  const theme = useSelector(({ theme }: GlobalState) => theme)
  const { programStyle, body, costText } = styles(theme)
  //flatlist renderItem 함수
  const renderPrograms = ({ item }) => {
    if (isLoading) return <Text>loading...</Text>
    //prettier-ignore
    const onPress = (id:string) => () => {
      console.log(id)
      setModalVisible(true)
  }
    return (
      <TouchableView
        onPress={onPress(item.program_id)}
        viewStyle={programStyle}
      >
        <DefaultText text={item.program_name} />
        <DefaultText text={item.description} />
        <DefaultText text={`가격 : ${item.price}`} textStyle={costText} />

        <Schedule
          visible={modalVisible}
          transparent={true}
          programId={item.program_id}
          setModalVisible={setModalVisible}
          programName={item.program_name}
          programDetail={item.description}
          thumbnail={item.thumbnail}
          price={item.price}
        />
      </TouchableView>
    )
  }
  return (
    <FlatList
      data={data}
      renderItem={renderPrograms}
      keyExtractor={(item) => item.program_id}
      style={body}
    />
  )
}

const styles = (theme: DefaultTheme) => {
  const { content, text, mainBackground } = theme
  const shadowOption = isLight(theme) ? shadow : {}
  return StyleSheet.create({
    body: {
      padding: 5,
    },
    programStyle: {
      backgroundColor: content,
      height: 100,
      marginBottom: 10,
      padding: 5,
      borderRadius: 17,
      justifyContent: 'space-between',
      ...shadowOption,
    },
    costText: {
      fontSize: 10,
      alignSelf: 'flex-end',
    },
  })
}

export default Program
