import { DailyProps } from '@/utils/data';
import React from 'react';
import styled from 'styled-components/native';

const Container = styled.TouchableOpacity`
    width: 120;
    height: 120;
    padding: 10px;
    padding-top: 5px;
    border-radius: 15;
    border-width: .2;
    shadow-color: #000000;
    shadow-opacity: 0.8;
    shadow-radius: 2;
    elevation: 1;
`

interface DateTextProps {
    // 0
    daytype: 0 | 1 | 2;
}
const DateText = styled.Text<DateTextProps>`
    ${({date}) => {
        if(date === 0 || date === 6)
            return `color: ${date === 0 ? 'red' : 'blue'};`
    }}
`;


const Daily = ({}) => {
    const {date, events}:DailyProps = daily_dummy;
    return (
        <Container>
            <DateText day={date.getDay()}>
                {date.getDate()}
            </DateText>
        </Container>
    )
}

const daily_dummy:DailyProps = {
    date: new Date(),
    events:[
    {
        dateof: 1,
        title: "런닝머신 30분",
        content: "9km/h 5분 12km/h 5분 반복",
        icon: "running",
        date: new Date(),
        completed: false,
        color: "#ffbbbb"
    },
    {
        dateof: 1,
        title: "영단어 100개 암기",
        content: "Toeic 영단어",
        icon: "study",
        date: new Date(),
        completed: false,
        color: "#bbbbff"
    },
    ]
}

export default Daily;
