import { GlobalState } from "@/../App";
import { IDailyList, IDaily } from "@/utils/data";
import { Dayjs } from "dayjs";
import React from "react";
import { useRef } from "react";
import { useState } from "react";
import { useEffect } from "react";
import { Dimensions, FlatList, StyleSheet } from "react-native";
import { useSelector } from "react-redux";
import { DefaultTheme } from "styled-components/native";
import Daily from "./Daily";

interface DailyListProps extends IDailyList {
    loading: Boolean;
    selector: (index:number) => void;
    loader: (after:boolean) => Promise<void>;
}
function DailyList(props: DailyListProps){
    const {start, end, data, loading, loader, selector} = props;

    const theme = useSelector((state:GlobalState) => state);

    // 최적화 필요!!!
    const [dailylist, setDailyList] = useState<IDaily[]>([]);

    useEffect(() => {
        if(loading) return;
        let list:IDaily[] = getDailyBoxes(start, end);
        setDailyList(list);
        setCurrentStart(start);
        setCurrentEnd(end);
    }, [loading])

    useEffect(() => {
        if(start >= currentStart)
            return;
        let newDailylist:IDaily[] = getDailyBoxes(start, currentStart.subtract(1, 'day'))
        setDailyList( prev => newDailylist.concat(prev))
        setCurrentStart(start);
    }, [start])

    useEffect(() => {
        if(currentEnd >= end || currentStart == currentEnd)
            return;
        let newDailylist:IDaily[] = getDailyBoxes(currentEnd.add(1, 'day'), end);
        setDailyList( prev => prev.concat(newDailylist));
        setCurrentEnd(end);
    }, [end])

    const [contentSize, setContentSize] = useState<number>(0);
    const getData = async (props:any) => {
        if(loading) return;
        // loader(true);

        const x = props.nativeEvent.contentOffset.x;
        if(x < 0)
            loader(false);
        else if(contentSize - x < 300)
            loader(true);
    }
    const renderItem = (props:any) => {
        if(loading) return <></>;
        const onPress = () => selector(props.index)
        return (
            <Daily 
                onPress={onPress}
                {...props.item} 
            />)
    }

    return(
        <FlatList
            onScrollEndDrag={getData}
            onContentSizeChange={setContentSize}
            horizontal
            showsHorizontalScrollIndicator
            initialNumToRender={7}
            style={styles(theme).scroll}
            data={dailylist}
            keyExtractor={(item) => String(item.date.format("YYYYMMDD") )}
            renderItem={renderItem}
            />
    )
}


const styles = (theme:DefaultTheme) => {
    return StyleSheet.create({
        scroll:{
            flex: 1,
            padding: 5,
            paddingVertical: 10,
            backgroundColor: "#fafafa",
        },
    })
}


export default DailyList;
