import axios from 'axios'

//초기 상태
interface userAuthProps {
  loading: boolean
  error: any
  token: string
  username: string
  password: string
  status: boolean
  address?: string
  nickname?: string
  tel?: string
  email?: string
  gender?: 'M' | 'F'
}

const userState: userAuthProps = {
  token: '',
  username: '',
  password: '',
  error: '',
  loading: false,
  status: false,
}

//함수 인터페이스
interface loginOrRegisterProps {
  username: string
  password: string
}

interface setProfileProps {
  address: string
  nickname: string
  tel: string
  email: string
  gender: 'M' | 'F'
}

interface logoutProps {
  token: string
}

// 액션 타입

// 로그인
const LOGIN = 'user/LOGIN'
const LOGIN_SUCCESS = 'user/LOGIN_SUCCESS'
const LOGIN_ERROR = 'user/LOGIN_ERROR'

const REGISTER = 'user/REGISTER'
const REGISTER_SUCCESS = 'user/REGISTER_SUCCESS'
const REGISTER_ERROR = 'user/REGISTER_ERROR'

const SET_PROFILE = 'user/SET_PROFILE'

const LOGOUT = 'user/LOGOUT'
const LOGOUT_SUCCESS = 'user/LOGOUT_SUCCESS'
const LOGOUT_ERROR = 'user/LOGOUT_ERROR'

//액션 생성함수
export const login =
  ({ username, password }: loginOrRegisterProps) =>
  async (dispatch) => {
    dispatch({ type: LOGIN })
    try {
      const token = await axios.post(
        'http://3.35.169.23:8000/account/auth/login/',
        {
          username,
          password,
        }
      )
      dispatch({ type: LOGIN_SUCCESS, token })
    } catch (e) {
      dispatch({ type: LOGIN_ERROR, error: e })
    }
  }

export const register =
  ({ username, password }: loginOrRegisterProps) =>
  async (dispatch) => {
    dispatch({ type: REGISTER })
    try {
      const token = await axios.post(
        'http://3.35.169.23:8000/account/auth/register/',
        {
          username,
          password,
        }
      )
      dispatch({ type: REGISTER_SUCCESS, token })
    } catch (e) {
      dispatch({ type: REGISTER_ERROR, error: e })
    }
  }
//prettier-ignore
export const setProfile = ({address, nickname,tel, email,gender}:setProfileProps) => ({
  type: SET_PROFILE,
  body:{
    address,
    nickname,
    tel,
    email,
    gender
  }
})

export const logout =
  ({ token }: logoutProps) =>
  async (dispatch) => {
    dispatch({ type: LOGOUT })
    try {
      await axios.post(
        'http://3.35.169.23:8000/account/auth/logout/',
        {},
        {
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Token ${token}`,
          },
        }
      )
      dispatch({ type: LOGIN_SUCCESS })
    } catch (e) {
      dispatch({ type: LOGOUT_ERROR, error: e })
    }
  }

function userAuth(state: userAuthProps = userState, action) {
  switch (action.type) {
    case LOGIN:
      return {
        ...state,
        loading: true,
      }
    case LOGIN_SUCCESS:
      return {
        ...state,
        token: action.token,
        status: true,
      }
    case LOGIN_ERROR:
      return {
        ...state,
        error: action.error,
      }
    case REGISTER:
      return {
        ...state,
        loading: true,
      }
    case REGISTER_SUCCESS:
      return {
        ...state,
        token: action.token,
      }
    case SET_PROFILE:
      return {
        ...state,
        error: action.error,
      }
    case LOGOUT:
      return {
        ...state,
        loading: true,
      }
    case LOGOUT_SUCCESS:
      return {
        ...state,
        token: '',
        status: false,
      }
    case LOGOUT_ERROR:
      return { ...state, error: action.error }
    default:
      return state
  }
}
export default userAuth
