import { DailyProps, daytype, User_Event } from '@/utils/data';
import { getDayType } from '@/utils/date';
import React from 'react';
import EventView from './EventView';
import { StyleSheet, TouchableOpacity, TouchableOpacityProps, Text } from 'react-native'
import { AppState } from '@/../App';
import { useSelector } from 'react-redux';
import { DefaultTheme } from '@/style/styled';

function Daily ({}) {
    //Dummy Variable
    const {date, events}:DailyProps = daily_dummy;

    const theme = useSelector(({theme}:AppState) => theme);
    const daytype:daytype = getDayType(date);
    const {container, datetext} = styles(theme, {daytype});

    return (
        <TouchableOpacity style={container}>
            <Text style={datetext}>
                {date.getDate()}
            </Text>
            <EventView {...events[0]} />
            <EventView {...events[1]} />
        </TouchableOpacity>
    )
}

interface DailyStyleProps {
    daytype: daytype
}

const styles = (theme:DefaultTheme, {daytype}:DailyStyleProps) => {
    const {mode_name, mainBackground, text, border} = theme;
    const isLight = Boolean(mode_name.match("light"));
    const container_base = {
        backgroundColor: mainBackground,
        width: 120,
        height: 120,
        padding: 5,
        paddingTop: 3,
        borderRadius: 20,
        borderWidth: 1
    }
    const shadow = {
        shadowColor: "#000000",
        shadowOpacity: 0.8,
        shadowRadius: 2,
        elevation: 1
    }
    return StyleSheet.create({
        container: isLight ? {...container_base, ...shadow} : container_base,
        datetext: {
            color: daytype === 0 ? text : daytype === 1 ?'blue' : 'red',
            fontWeight: '800',
            fontSize: 20
        }
    })
}

const daily_dummy:DailyProps = {
    date: new Date(),
    events:[
    {
        dateof: 1,
        title: "런닝머신 30분",
        abb: "런닝머신",
        content: "9km/h 5분 12km/h 5분 반복",
        icon: "running",
        date: new Date(),
        completed: false,
        color: "#ffc8c8"
    },
    {
        dateof: 1,
        title: "영단어 100개 암기",
        content: "Toeic 영단어",
        abb: "영단어",
        icon: "study",
        date: new Date(),
        completed: false,
        color: "#bbbbff"
    },
    ]
}

export default Daily;
