    color?: "primary" | "secondary" | "ghost"
    disabled?: boolean
    content: string
  }
const styles = (theme:DefaultTheme, props:ButtonProps) => {
    const {main, text, border, disabled} = props.color ? theme[props.color] : theme.primary;
