import React from 'react'; 
import { User_Event } from '@/utils/data';
import { View, Text, StyleSheet } from 'react-native';
import styled from 'styled-components/native';
import { DefaultTheme } from '@/style/styled';
import { useSelector } from 'react-redux';
import { AppState } from '@/../App';

interface EventViewProps extends User_Event {

}

function EventView (props:EventViewProps){
    const {color, title} = props;
    const theme = useSelector(({theme}:AppState) => theme);

    const {container, circleContainer, circle, content} = styles(theme, {color});
    console.log(color);
    return(
        <Container >
            <View style={circleContainer}>
                <View style={{...styles.circle, backgroundColor:color}} />
            </View>
            <Text style={styles.content}>
                {title}
            </Text>
        </Container>
    )
}

interface EventViewStyleProps {
    color: string
}
const styles = (theme:DefaultTheme, {}:EventViewStyleProps) =>  StyleSheet.create({
    container:{
        height: 20,
        flexDirection: 'row'
    },
    circleContainer: {
        flex: 1,
        paddingVertical: 2,
        paddingLeft: 0,
        paddingRight: 4
    },
    circle: {
      flex: 1,
      borderRadius: 50,
    },
    content: {
        flex:5.3
    }
  });

export default EventView;
