import { GlobalState } from "@/../App";
import { DailyListProps, DailyProps, User_Event } from "@/utils/data";
import { Dayjs } from "dayjs";
import React from "react";
import { useState } from "react";
import { useEffect } from "react";
import { View, Dimensions, FlatList, StyleSheet, ViewProps } from "react-native";
import { useSelector } from "react-redux";
import { DefaultTheme } from "styled-components/native";
import Daily from "./Daily";

interface DailyListComponentProps extends DailyListProps {
    loader: (after:boolean) => () => Promise<void>
}
function DailyList(props: DailyListComponentProps){
    const {start, end, loader} = props;
    const totalEvents = props.events;
    const [currentStart, setCurrentStart] = useState<Dayjs>(start);
    const [currentEnd, setCurrentEnd] = useState<Dayjs>(end);
    const theme = useSelector((state:GlobalState) => state);

    // 최적화 필요!!!
    const [dailylist, setDailyList] = useState<DailyProps[]>([]);

    const updateDaylistEvents = (list:DailyProps[], from:Dayjs, to:Dayjs):DailyProps[] => {
        console.log(list);
        console.log(totalEvents);
        totalEvents
            .filter(({date}:User_Event) => 
                date >= from && date <= to)
            .forEach( (event:User_Event) => {
                const date = event.date;
                list[date.diff(from, 'days')].events.push(event);
            })
        return list;
    }

    useEffect(() => {
        const diff = end.diff(start, 'days');
        let list:DailyProps[] = [];
        for(let i = 0; i<=diff; i++){
            const events:User_Event[] = [];
            list.push({
                date: start.add(i, 'days'),
                events
            });
        }
        list = updateDaylistEvents(list, start, end);
        setDailyList(list);
    }, [])

    useEffect(() => {
        if(start >= currentStart)
            return;
        let newDailylist:DailyProps[] = [];
        const diff = start.diff(currentStart, 'days');
        for(let i = 0; i<diff; i++){
            const events:User_Event[] = [];
            newDailylist.push({
                date: start.add(i, 'days'),
                events
            });
        }
        newDailylist = updateDaylistEvents(newDailylist, start, currentStart.subtract(1, 'day'));
        setDailyList( prev => newDailylist.concat(prev))
        setCurrentStart(start);
    }, [start])
    useEffect(() => {
        if(currentEnd >= end)
            return;
        let newDailylist:DailyProps[] = [];
        const diff = currentEnd.diff(end, 'days');
        for(let i = 0; i<diff; i++){
            const events:User_Event[] = [];
            newDailylist.push({
                date: currentEnd.add(i, 'days'),
                events
            });
        }
        newDailylist = updateDaylistEvents(newDailylist, currentEnd.add(1, 'day'), end);
        setDailyList( prev => newDailylist.concat(prev));
        setCurrentEnd(end);
    }, [end])






    return(
        <FlatList
            horizontal
            showsHorizontalScrollIndicator
            onEndReached={loader(true)}
            style={styles(theme).scroll}
            data={dailylist}
            keyExtractor={(item) => String(item.date.format("YYYYMMDD") )}
            renderItem={renderItem}
            onEndReachedThreshold={0.3}
            />
    )
}

const renderItem = ({item}) => {
    return <Daily {...item} />
}

const styles = (theme:DefaultTheme) => {
    const screenWidth = Dimensions.get('window').width;
    return StyleSheet.create({
        scroll:{
            height: 20,
            flexDirection: 'row',
            minWidth:screenWidth,
            padding: 5,
            paddingVertical: 15,
            backgroundColor: "#fafafa",
        },
    })
}


export default DailyList;
