import { DailyProps, daytype } from '@/utils/data';
import { getDayType } from '@/utils/date';
import React, { useMemo } from 'react';
import EventView from './EventView';
import { StyleSheet, TouchableOpacity, Text } from 'react-native'
import { useSelector } from 'react-redux';
import { DefaultTheme } from '@/style/styled';
import { isLight } from '@/style/themes';
import { GlobalState } from '@/../App';
import { shadow } from '@/style/style-util';
import dayjs from 'dayjs';
import { useEffect } from 'react';

function Daily ({date, events }:DailyProps) {
    const theme = useSelector(({theme}:GlobalState) => theme);
    const daytype:daytype = getDayType(date);
    const {container, datetext, extra} = styles(theme, {daytype});

    const today = dayjs();
    useEffect(() => {
        console.log('hello');
    }, [])
    // return useMemo(() => (
    //     <TouchableOpacity style={container}>
    //         <Text style={datetext}>
    //             { date.month()== today.month() && date.year()== today.year() ?
    //                 date.date() : `${date.month()+1}/${date.date()}`
    //             }
    //         </Text>
    //         {
    //             events
    //                 .filter((_, i) => i<3)
    //                 .map( (event, idx) => 
    //                     <EventView 
    //                         key={idx} 
    //                         {...event}
    //                     />)
    //         }
    //         {
    //             events.length > 3 && 
    //                 <Text style={extra}> 
    //                     +{events.length-3} 
    //                 </Text>
    //         }
    //     </TouchableOpacity>
    // ), []);
    return (
        <TouchableOpacity style={container}>
            <Text style={datetext}>
                { date.month()== today.month() && date.year()== today.year() ?
                    date.date() : `${date.month()+1}/${date.date()}`
                }
            </Text>
            {
                events
                    .filter((_, i) => i<3)
                    .map( (event, idx) => 
                        <EventView 
                            key={idx} 
                            {...event}
                        />)
            }
            {
                events.length > 3 && 
                    <Text style={extra}> 
                        +{events.length-3} 
                    </Text>
            }
        </TouchableOpacity>)
}

interface DailyStyleProps {
    daytype: daytype
}

const styles = (theme:DefaultTheme, {daytype}:DailyStyleProps) => {
    const {mode_name, content, text} = theme;
    Boolean(mode_name.match("light"));
    const container_base = {
        backgroundColor: content,
        width: 120,
        height: 120,
        padding: 5,
        paddingTop: 3,
        borderRadius: 17,
        marginRight: 8,
    };
    const container = isLight(theme) ? {...container_base, ...shadow} : container_base;
    return StyleSheet.create({
        container,
        datetext: {
            color: daytype === 0 ? text : daytype === 1 ?'blue' : 'red',
            fontWeight: '800',
            fontSize: 20,
        },
        extra:{
            textAlign: "center"
        }
    })
}


export default Daily;
