import { AppState } from '@/../App';
import { DefaultTheme } from '@/style/styled';
import React from 'react';
import { StyleSheet, TouchableOpacity, TouchableOpacityProps, Text } from 'react-native'
import { useSelector } from 'react-redux';

interface ButtonProps extends TouchableOpacityProps{
    color?: "primary" | "secondary" | "ghost"
    content: string
}


const Button = function(props:ButtonProps){
    const theme = useSelector(({theme}:AppState) => theme);
    const {container, content} = styles(theme, props);
    const {color, ...others} = props;
  return(
    <TouchableOpacity style={container} {...others}>
      <Text style={content}>
        {props.content}
      </Text>
    </TouchableOpacity>
  )
}
export default Button;
