import { isSolarHioliday} from 'holiday-kr';

export const getDayType = (date:Date) => {
    // 0 : 평일 , 1: 토요일, 2: 일요일, 공휴일
    const day = date.getDay();
    if(day === 0 || isSolarHioliday(day))
        return 2;
    else
        return day === 6 ? 1 : 0;
}
