import { DefaultTheme } from "./styled";

type themeType = "light" | "dark";
interface changeThemeAction {
    type: themeType;
}

const changeTheme = (type:themeType = "light"):changeThemeAction => ({type});
export const actionCreators = {
    changeTheme
}

const theme = (state:DefaultTheme = light, action:changeThemeAction):DefaultTheme => {
    switch(action.type){
        case "light": return light;
        case "dark": return dark;
    }
}
export default store

const light: DefaultTheme = {
    mainBackground: `#fff`,
    text: "#111111",
    title: "#000000",
    disabled: `rgba(255,255,255,0.25)`,
    primary: {
        main: "#f1a150",
        text: `#fff`,
        border: "transparent",
        disabled: '#888888'
    },
    secondary:{
        main: "#ffdcb9",
        text: `rgba(255,255,255,0.85)`,
        border: "transparent",
        disabled: '#4A4A4A',
    },
    ghost: {
        main: `rgba(255,255,255,0)`,
        text: '#ff9425',
        border: "#000000",
        disabled: `rgba(255,255,255,0)`
    }
}
const dark: DefaultTheme = {
    mainBackground: `#333`,
    text: "#ddd",
    title: `rgba(255,255,255,0.85)`,
    disabled: `rgba(255,255,255,0.25)`,
    primary: {
        main: "#f1a150",
        text: `#fff`,
        border: "transparent",
        disabled: '#888888'
    },
    secondary:{
        main: "#ffdcb9",
        text: `rgba(255,255,255,0.85)`,
        border: "transparent",
        disabled: '#4A4A4A',
    },
    ghost: {
        main: `rgba(255,255,255,0)`,
        text: '#ff9425',
        border: "#000000",
        disabled: `rgba(255,255,255,0)`
    }
}
