import { DailyProps, daytype, User_Event } from '@/utils/data';
import { getDayType } from '@/utils/date.js';
import React from 'react';
import styled from 'styled-components/native';
import EventView from './EventView';

const DailyContainer = styled.TouchableOpacity`
    width: 120;
    height: 120;
    padding: 5px;
    padding-top: 3px;
    border-radius: 15;
    border-width: .2;
    shadow-color: #000000;
    shadow-opacity: 0.8;
    shadow-radius: 2;
    elevation: 1;
`

interface DateTextProps {

}

const DateText = styled.Text<DateTextProps>`
    font-weight: 800;
    font-size: large;
    ${({daytype}) => {
        if(daytype > 0)
            return `color: ${daytype === 2 ? 'red' : 'blue'};`
    }}
`;


function Daily ({}) {
    const {date, events}:DailyProps = daily_dummy;
    return (
        <DailyContainer>
            <DateText daytype={getDayType(date)}>
                {date.getDate()}
            </DateText>
            <EventView {...events[0]} />
        </DailyContainer>
    )
}

interface DailyStyleProps {

}

const daily_dummy:DailyProps = {
    date: new Date(),
    events:[
    {
        dateof: 1,
        title: "런닝머신 30분",
        content: "9km/h 5분 12km/h 5분 반복",
        icon: "running",
        date: new Date(),
        completed: false,
        color: "#9c4646"
    },
    {
        dateof: 1,
        title: "영단어 100개 암기",
        content: "Toeic 영단어",
        icon: "study",
        date: new Date(),
        completed: false,
        color: "#bbbbff"
    },
    ]
}

export default Daily;
