import { DailyListProps, DailyProps, User_Event } from "@/utils/data";
import moment from "moment";
import React from "react";
import { AppState, Dimensions, ScrollView, StyleSheet } from "react-native";
import { useSelector } from "react-redux";
import { DefaultTheme } from "styled-components/native";
import Daily from "./Daily";

function DailyList(){
    //Dummy Variable
    const props = dailylist_dummy;
    const {start, end, events} = props;

    // 최적화 필요!!!
    const dailylist:DailyProps[] = [];
    const diff = start.diff(end, 'days');
    for(let i = 0; i<=diff; i++){
        dailylist.push({
            date: start.add(i, 'days'),
            events: []
        })
    }
    events.map( (event:User_Event) => {
        const date = event.date;
        dailylist[date.diff(start)].events.push(event);
    })

    const theme = useSelector((state:AppState) => state);
    return(
        <ScrollView
            horizontal
            showsHorizontalScrollIndicator
            style={styles(theme).container}
            >
        {
            dailylist.map(({date, events}) )
        }
        </ScrollView>
    )
}

const styles = (theme:DefaultTheme) => {
    const screenWidth = Dimensions.get('window').width;
    return StyleSheet.create({
        container:{
            minWidth: screenWidth,
            padding: 5,
            backgroundColor: "#dddddd"
        }
    })
}

const dailylist_dummy:DailyListProps = {
    start: moment().subtract(1, 'days'),
    end: moment().add(7, 'days'),
    events:[
        {
            dateof: 1,
            title: "런닝머신 30분",
            abb: "런닝머신",
            content: "9km/h 5분 12km/h 5분 반복",
            icon: "running",
            date: moment(),
            completed: false,
            color: "#ffc8c8"
        },
        {
            dateof: 1,
            title: "영단어 100개 암기",
            content: "Toeic 영단어 1~100",
            abb: "영단어",
            icon: "study",
            date: moment(),
            completed: false,
            color: "#bbbbff"
        },
        {
            dateof: 2,
            title: "영단어 100개 암기",
            content: "Toeic 영단어 101~200",
            abb: "영단어",
            icon: "study",
            date: moment().add(1, 'days'),
            completed: false,
            color: "#bbbbff"
        },
        {
            dateof: 3,
            title: "영단어 100개 암기",
            content: "Toeic 영단어 201~300",
            abb: "영단어",
            icon: "study",
            date: moment().add(2, 'days'),
            completed: false,
            color: "#bbbbff"
        },
    ]
}
export default DailyList;
