import { DailyListProps, User_Event } from "@/utils/data";
import React from "react";
import { AppState, Dimensions, ScrollView, StyleSheet } from "react-native";
import { useSelector } from "react-redux";
import { DefaultTheme } from "styled-components/native";
import Daily from "./Daily";

function DailyList(){
    //Dummy Variable
    const dailylist = dailylist_dummy;


    const theme = useSelector((state:AppState) => state);
    return(
        <ScrollView
            horizontal
            showsHorizontalScrollIndicator
            style={styles(theme).container}
            >
        </ScrollView>
    )
}

const styles = (theme:DefaultTheme) => {
    const screenWidth = Dimensions.get('window').width;
    return StyleSheet.create({
        container:{
            minWidth: screenWidth,
            padding: 5,
            backgroundColor: "#dddddd"
        }
    })
}

const dailylist_dummy:DailyListProps = {
    start: new Date(),
    events:[
        {
            dateof: 1,
            title: "런닝머신 30분",
            abb: "런닝머신",
            content: "9km/h 5분 12km/h 5분 반복",
            icon: "running",
            date: new Date(),
            completed: false,
            color: "#ffc8c8"
        },
        {
            dateof: 1,
            title: "영단어 100개 암기",
            content: "Toeic 영단어",
            abb: "영단어",
            icon: "study",
            date: new Date(),
            completed: false,
            color: "#bbbbff"
        }
    ]
}
export default DailyList;
