import { InputView } from '@/components/Input'
import { TouchableView } from '@/components/TouchableView'
import { GlobalState } from '@/modules'
import { DefaultTheme } from '@/style/styled'
import React, { useState } from 'react'
import { Alert, TextInput } from 'react-native'
import { Modal, StyleSheet, View, Text, SafeAreaView } from 'react-native'
import { useSelector } from 'react-redux'

import { getMarketPrograms, programs_dummy } from '@/db/market/MarketPrograms'
import PlanSearch from './PlanSearch'
import { IProgram } from '@/utils/data'
import { useEffect } from 'react'
import { FlatList } from 'react-native'
import PlanList from './PlanList'
interface MarketProps {}
function Market({}: MarketProps) {
  const theme = useSelector(({ theme }: GlobalState) => theme)
  const { body } = styles(theme)
  const [modalVisible, setModalVisible] = useState(false)
  const [programs, setPrograms] = useState<IProgram[]>([])
  const [isLoading, setIsLoading] = useState(true)
  const ModalOpen = () => {
    setModalVisible((prev) => !prev)
  }
  //prettier-ignore
  useEffect(() => {
    (async () => {
      //Fetch Programs
      const newPrograms: IProgram[] = await getMarketPrograms()
      setPrograms(newPrograms)
      setIsLoading(false)
      
    })()}
  ,[])

  return (
    <SafeAreaView style={body}>
      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => {
          Alert.alert('Modal has been closed.')
          ModalOpen()
        }}
      >
        <View
          style={{
            flex: 1,
            backgroundColor: 'rgba(0,0,0,.3)',
            alignItems: 'center',
            justifyContent: 'center',
          }}
        >
          <View
            style={{
              backgroundColor: '#fff',
              width: 100,
              height: 100,
              borderRadius: 18,
              padding: 10,
            }}
          >
            <Text>Modal Test</Text>
            <TouchableView onPress={ModalOpen}>
              <Text>close</Text>
            </TouchableView>
          </View>
        </View>
      </Modal>
      <PlanSearch />
      <View>
        <PlanList data={programs} isLoading={isLoading} />
      </View>
    </SafeAreaView>
  )
}

const styles = (theme: DefaultTheme) => {
  const { content, text } = theme
  return StyleSheet.create({
    body: {
      flex: 1,
      backgroundColor: theme.mainBackground,
      padding: 5,
    },
  })
}

export default Market
