import { DailyListProps, DailyProps, User_Event } from "@/utils/data";
import moment from "moment";
import React from "react";
import { AppState, Dimensions, ScrollView, StyleSheet } from "react-native";
import { useSelector } from "react-redux";
import { DefaultTheme } from "styled-components/native";
import Daily from "./Daily";

function DailyList({start, end, events}: DailyListProps){
    console.log(events);
    // 최적화 필요!!!
    const dailylist:DailyProps[] = [];
    const diff = start.diff(end, 'days');
    for(let i = 0; i<=diff; i++){
        dailylist.push({
            date: start.add(i, 'days'),
            events: []
        })
    }

    events.forEach( (event:User_Event) => {
        const date = event.date;
        dailylist[date.diff(start)].events.push(event);
    })

    const theme = useSelector((state:AppState) => state);
    return(
        <ScrollView
            horizontal
            showsHorizontalScrollIndicator
            style={styles(theme).container}
            >
        {
            dailylist.map((daily:DailyProps) => 
                <Daily {...daily} />
            )
        }
        </ScrollView>
    )
}

const styles = (theme:DefaultTheme) => {
    const screenWidth = Dimensions.get('window').width;
    return StyleSheet.create({
        container:{
            minWidth: screenWidth,
            padding: 5,
            backgroundColor: "#dddddd"
        }
    })
}


export default DailyList;
