import { GlobalState } from "@/../App";
import { DailyListProps, DailyProps } from "@/utils/data";
import { Dayjs } from "dayjs";
import React from "react";
import { useRef } from "react";
import { useState } from "react";
import { useEffect } from "react";
import { Dimensions, FlatList, StyleSheet } from "react-native";
import { useSelector } from "react-redux";
import { DefaultTheme } from "styled-components/native";
import Daily from "./Daily";

interface DailyListComponentProps extends DailyListProps {
    loading: Boolean;
    loader: (after:boolean) => Promise<void>;
}
function DailyList(props: DailyListComponentProps){
    const {start, end, data, loader, loading} = props;
    const [currentStart, setCurrentStart] = useState<Dayjs>(start);
    const [currentEnd, setCurrentEnd] = useState<Dayjs>(end);
    const theme = useSelector((state:GlobalState) => state);

    // 최적화 필요!!!
    const [dailylist, setDailyList] = useState<DailyProps[]>([]);
    // 추가할 Daily Box의 배열
    const getDailyBoxes = (from:Dayjs, to:Dayjs):DailyProps[] => {
        const list:DailyProps[] = [];
        const diff = to.diff(from, 'days');
        const filteredData = data
                                .filter(({date}:DailyProps) => date >= from && date <= to)
                                .map(({date, events}:DailyProps) => ({[date.format("YYYYMMDD")] : events}));
        for(let i = 0; i<=diff; i++){
            const date = from.add(i, 'days');
            const events = filteredData[date.format("YYYYMMDD")] || [];
            list.push({date, events});
        }
        return list;
    }

    useEffect(() => {
        if(loading) return;
        let list:DailyProps[] = getDailyBoxes(start, end);
        setDailyList(list);
        setCurrentStart(start);
        setCurrentEnd(end);
    }, [loading])

    useEffect(() => {
        if(start >= currentStart)
            return;
        let newDailylist:DailyProps[] = getDailyBoxes(start, currentStart.subtract(1, 'day'))
        setDailyList( prev => newDailylist.concat(prev))
        setCurrentStart(start);
    }, [start])

    useEffect(() => {
        if(currentEnd >= end || currentStart == currentEnd)
            return;
        let newDailylist:DailyProps[] = getDailyBoxes(currentEnd.add(1, 'day'), end);
        setDailyList( prev => prev.concat(newDailylist));
        setCurrentEnd(end);
    }, [end])

    const [contentSize, setContentSize] = useState<number>(0);
    const getData = async (props:any) => {
    // const getData = async (x:number) => {
        // props.nativeEvent.contentOffset.x
    // const getData = async () => {
        if(loading) return;
        // console.log(x);
        // const perc = x / contentSize;
        // console.log(perc);
        // if(perc < 0.01)
        //     loader(false);
        // else if(perc>0.9)
        //     loader(true);
        // loader(true);
    }
    const renderItem = (props:any) => {
        if(loading) return <></>;
        return <Daily {...props.item} />
    }

    return(
        <FlatList
            onScrollEndDrag={(props) => getData(props)}
            onContentSizeChange={setContentSize}
            horizontal
            showsHorizontalScrollIndicator
            initialNumToRender={7}
            // onEndReached={getData}
            // onEndReachedThreshold={0.001}
            style={styles(theme).scroll}
            data={dailylist}
            keyExtractor={(item) => String(item.date.format("YYYYMMDD") )}
            renderItem={renderItem}
            />
    )
}


const styles = (theme:DefaultTheme) => {
    const screenWidth = Dimensions.get('window').width;
    return StyleSheet.create({
        scroll:{
            height: 20,
            flexDirection: 'row',
            minWidth:screenWidth,
            padding: 5,
            paddingVertical: 15,
            backgroundColor: "#fafafa",
        },
    })
}


export default DailyList;
