import React from 'react'; 
import { User_Event } from '@/utils/data';
import { View, Text, StyleSheet } from 'react-native';
import styled from 'styled-components/native';
import { DefaultTheme } from '@/style/styled';
import { useSelector } from 'react-redux';
import { AppState } from '@/../App';

interface EventViewProps extends User_Event {

}

function EventView (props:EventViewProps){
    const {color, title} = props;
    const theme = useSelector(({theme}:AppState) => theme);
    const {container, circleContainer, circle, content} = styles(theme, {color});
    return(
        <View style={container} >
            <View style={circleContainer}>
                <View style={circle} />
            </View>
            <Text style={content}>
                {title}
            </Text>
        </View>
    )
}

interface EventViewStyleProps {
    color: string
}
const styles = (theme:DefaultTheme, {color}:EventViewStyleProps) =>  {
    const {mainBackground, text} = theme;
    return StyleSheet.create({
        container:{
            backgroundColor: mainBackground,
            height: 20,
            flexDirection: 'row'
        },
        circleContainer: {
            flex: 1,
            paddingVertical: 3,
            paddingLeft: 2,
            paddingRight: 6
        },
        circle: {
            backgroundColor: color,
            flex: 1,
            borderRadius: 50,
        },
        content: {
            flex:5.3,
            color: text
        }
  })
};

export default EventView;
