import { InputView } from '@/components/Input'
import { TouchableView } from '@/components/TouchableView'
import { GlobalState } from '@/modules'
import { DefaultTheme } from '@/style/styled'
import React, { useState } from 'react'
import { Alert, TextInput } from 'react-native'
import { Modal, StyleSheet, View, Text, SafeAreaView } from 'react-native'
import { useSelector } from 'react-redux'

import { getMarketPrograms, programs_dummy } from '@/db/market/MarketPrograms'
import { Image } from 'react-native'
import PlanSearch from './PlanSearch'
import { IProgram } from '@/utils/data'
import { useEffect } from 'react'
import { FlatList } from 'react-native'
interface MarketProps {}
function Market({}: MarketProps) {
  const theme = useSelector(({ theme }: GlobalState) => theme)
  const { img, item, body, search, input } = styles(theme)
  const [text, onChangeText] = useState('')
  const [modalVisible, setModalVisible] = useState(false)
  const [programs, setPrograms] = useState<IProgram[]>([])
  const [isLoading, setIsLoading] = useState(true)
  const ModalOpen = () => {
    setModalVisible((prev) => !prev)
  }
  //prettier-ignore
  useEffect(() => {
    (async () => {
      //Fetch Programs
      const newPrograms: IProgram[] = await getMarketPrograms()
      setPrograms(newPrograms)
      setIsLoading(false)
    })}
  ,[])
  const renderItem = ({ item }) => {
    if (isLoading) return <Text>loading...</Text>
    return (
      <View>
        <Text>{item.program_name}</Text>
        <Text>{item.program_name}</Text>
        <Text>{item.program_name}</Text>
      </View>
    )
  }

  return (
    <SafeAreaView style={body}>
      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => {
          Alert.alert('Modal has been closed.')
          ModalOpen()
        }}
      >
        <View
          style={{
            flex: 1,
            backgroundColor: 'rgba(0,0,0,.3)',
            alignItems: 'center',
            justifyContent: 'center',
          }}
        >
          <View
            style={{
              backgroundColor: '#fff',
              width: 100,
              height: 100,
              borderRadius: 18,
              padding: 10,
            }}
          >
            <Text>Modal Test</Text>
            <TouchableView onPress={ModalOpen}>
              <Text>close</Text>
            </TouchableView>
          </View>
        </View>
      </Modal>
      <PlanSearch />
      <View>
        {/* 데이터는 AXIOS로 받아올 것이기 때문에 비동기 함수로 데이터를 받아올 것임.
                따라서 programs 데이터는 useState<IPrograms[]>([]) 활용해서 State로 선언해야 하고,
                useEffect 활용해서 데이터 받아올 것. 데이터 받아오는 동안은 로딩 화면이 나타나야 하니
                loading이라는 state 선언해서 데이터 받아오는 동안에는 로딩 화면 나타날 수 있도록.
              */}
        <FlatList data={programs} renderItem={renderItem} />
      </View>
    </SafeAreaView>
  )
}

const styles = (theme: DefaultTheme) => {
  const { content, text } = theme
  return StyleSheet.create({
    body: {
      flex: 1,
      backgroundColor: theme.mainBackground,
      padding: 5,
    },
    search: {
      backgroundColor: theme.mainBackground,
      height: 50,
      flexDirection: 'row',
      alignItems: 'center',
      marginBottom: 10,
    },
    input: {
      flex: 1,
    },
    item: {
      borderWidth: 1,
      borderColor: 'black',
      marginBottom: 10,
      display: 'flex',
      flexDirection: 'row',
      backgroundColor: content,
    },
    itemText: {
      display: 'flex',
      flexWrap: 'wrap',
    },
    img: {
      height: 100,
      width: 100,
      marginRight: 10,
    },
  })
}

export default Market
