import { DailyProps, daytype, User_Event } from '@/utils/data';
import { getDayType } from '@/utils/date.js';
import React from 'react';
import EventView from './EventView';
import { StyleSheet, TouchableOpacity, TouchableOpacityProps, Text } from 'react-native'
import { AppState } from '@/../App';
import { useSelector } from 'react-redux';

function Daily ({}) {
    //Dummy Variable
    const {date, events}:DailyProps = daily_dummy;

    const theme = useSelector(({theme}:AppState) => theme);
    const datype = getDayType(date);
    const {container, datetext} = styles(theme, {daytype});

    return (
        <TouchableOpacity>
            <Text style={datetext}>
                {date.getDate()}
            </Text>
            <EventView {...events[0]} />
        </TouchableOpacity>
    )
}

interface DailyStyleProps {
    daytype: daytype
}

const styles = (theme:DefaultTheme, {daytype}:DailyStyleProps) => {
    const {text} = theme;
    return StyleSheet.create({
        container: {
            // width: "120px",
            // height: "120px",
            // padding: 5,
            // padding-top: 3,
            borderRadius: 15,
            borderWidth: .2,
            shadowColor: "#000000",
            shadowOpacity: 0.8,
            shadowRadius: 2,
            elevation: 1,
        },
        datetext: {
            color: daytype === 0 ? text : daytype === 1 ?'blue' : 'red',
            fontWeight: '800',
            fontSize: 20
        }
    })
}

const daily_dummy:DailyProps = {
    date: new Date(),
    events:[
    {
        dateof: 1,
        title: "런닝머신 30분",
        content: "9km/h 5분 12km/h 5분 반복",
        icon: "running",
        date: new Date(),
        completed: false,
        color: "#9c4646"
    },
    {
        dateof: 1,
        title: "영단어 100개 암기",
        content: "Toeic 영단어",
        icon: "study",
        date: new Date(),
        completed: false,
        color: "#bbbbff"
    },
    ]
}

export default Daily;
