import { GlobalState } from '@/modules'
import { DefaultTheme } from '@/style/styled'
import React from 'react'
import { FlatList, Text } from 'react-native'
import { StyleSheet, View } from 'react-native'
import { useSelector } from 'react-redux'
import MarketCategory from './MarketCategory'

const categories = ['헬스', '학습', '투자', '여행', '프로그래밍', '악기연주']

interface MarketMainProps {}

function MarketMain({}: MarketMainProps) {
  const { theme } = useSelector((state: GlobalState) => state)
  const { container, itemStyle, header } = styles(theme)
  const renderPrograms = ({ item }) => {
    return (
      <View style={itemStyle}>
        <Text>{item}</Text>
      </View>
    )
  }
  return (
    <View style={container}>
      <View style={header}>
        <Text>hello</Text>
      </View>
      <FlatList data={categories} renderItem={renderPrograms} numColumns={2} />
    </View>
  )
}

const styles = (theme: DefaultTheme) => {
  const { content, text } = theme
  return StyleSheet.create({
    container: { flex: 1 },
    header: {
      height: 80,
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
    },
    itemStyle: {
      width: 160,
      height: 160,
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      borderWidth: 2,
      borderColor: 'black',
    },
  })
}

export default MarketMain
